function build(a) {
                  new Ajax.Request(a.href);

                  hoverNotification('Build scheduled',a.parentNode);
                  return false;
                  }

function onDeleteClick() {
jQuery.ajax({
url: "/hudson/job/MyProject/api/json",
dataType: 'json',
cache: false,
success: function(data) {
jQuery('#confirmDialog').show();
jQuery('#buildInProgressDialog').hide();
jQuery('#cascadingChildrenPresentDiald').hide();
var cascadingChildren = data.cascadingChildrenNames;
if (cascadingChildren.length != 0) {
var jobList = jQuery("#jobList");
jobList.html("");
cascadingChildren.sort();
for (var i = 0; i < cascadingChildren.length; i++) {
jobList.append("<li>" + cascadingChildren[i] + "</li>");
}
jQuery('#confirmDialog').hide();
jQuery('#buildInProgressDialog').hide();
jQuery('#cascadingChildrenPresentDiald').show();
} else{
if(data.lastBuild.length != 0){
var lastBuildUrl = data.lastBuild.url + "api/json";
jQuery.ajax({
url: lastBuildUrl,
dataType: 'json',
cache: false,
success: function(buildData) {
if(buildData.building){
jQuery('#confirmDialog').hide();
jQuery('#cascadingChildrenPresentDiald').hide();
jQuery('#buildInProgressDialog').show();
}
}
});
}
}
}
});
onLinkClick("question");
}

tbody.script:
function encodeAllInputs(sep, form, field) {
var inputs = Form.getInputs(form, null, field);
if (inputs.length == 0)
return "";

var rv = sep;
for (var i = 0; i < inputs.length; ++i) {
if (i != 0)
rv += "&";
rv += field+"="+encode(inputs[i].value);
}
return rv;
}

tr.td:
Can use wildcards like 'module/dist/**/*.zip'. See the 
                                  @includes of Ant fileset for the exact format. The base directory is 
                                  the workspace.

